import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Room = props => (
    <tr>
        <td></td>
        <td>{props.rooms.name}</td>
        <td>{props.rooms.date_created}</td>
        <td>{props.rooms.edit_date}</td>
        <td>
            {/* <Link to={"/edit/"+props.todo._id}>Edit</Link> */}
        </td>
    </tr>
)

export default class Rooms extends Component {

    constructor(props){
        super(props);
        this.state = { rooms: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:4000/api/chatroom')
            .then(response => {
                this.setState({rooms: response.data});
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    componentDidUpdate() {
        // axios.get('http://localhost:4000/todos/')
        // .then(response => {
        //     this.setState({todos: response.data});
        // })
        // .catch(function (error) {
        //     console.log(error);
        // })
    }

    roomList() {
        return this.state.rooms.map(function(currentRoom, i) {
            return <Room room={currentRoom} key={i} />;
        });
    }

    render() {
        return(
            <div>
                <h3>Rooms</h3>
                <table className="table table-striped" style={{ marginTop: 20 }}>
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>Room</th>
                            <th>Created Date</th>
                            <th>Edit Date</th>
                            <th>Status</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.roomList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
